using UnityEngine;public static class CameraHelper{    public static byte[] CaptureFrame(Camera camera)  {    RenderTexture targetTexture = camera.targetTexture;    RenderTexture.active = targetTexture;    Texture2D texture2D = new Texture2D(CameraSensor.width, CameraSensor.height, TextureFormat.RGB24, false);	texture2D.ReadPixels(new Rect(0, 0, CameraSensor.width, CameraSensor.height), 0, 0);    texture2D.Apply();    byte[] image = texture2D.EncodeToJPG();    //byte[] image = texture2D.EncodeToPNG();    //byte[] image = texture2D.GetRawTextureData();    Object.DestroyImmediate(texture2D); // Required to prevent leaking the texture    return image;  }}